package com.example.moneytracker.data.db;import androidx.lifecycle.LiveData;import androidx.room.Dao;import androidx.room.Delete;import androidx.room.Insert;import androidx.room.Query;import androidx.room.Update;import com.example.moneytracker.data.model.AccountWithBalance;import java.util.List;@Daopublic interface AccountDao {    @Insert    void insertAccount(Account account);    @Update    void updateAccount(Account account);    @Delete    void deleteAccount(Account account);    @Query("SELECT *, " +            "(SELECT IFNULL(SUM(amount), 0) FROM transactions WHERE accountId = accounts.id) + initialBalance " +            "AS balance FROM accounts ORDER BY name ASC")    LiveData<List<AccountWithBalance>> getAccountsWithBalance();    @Query("SELECT * FROM accounts WHERE id = :accountId")    LiveData<Account> getAccountById(int accountId);}