package com.example.moneytracker.data.db;import androidx.room.Entity;import androidx.room.PrimaryKey;@Entity(tableName = "categories")public class Category {    @PrimaryKey(autoGenerate = true)    public int id;    public String name;    public String iconName;    public Category() {}    public Category(String name, String iconName) {        this.name = name;        this.iconName = iconName;    }}