package com.example.moneytracker.di;import com.example.moneytracker.data.repository.AccountRepository;import com.example.moneytracker.data.repository.CategoryRepository;import com.example.moneytracker.data.repository.TransactionRepository;import com.example.moneytracker.interfaces.IAccountRepository;import com.example.moneytracker.interfaces.ICategoryRepository;import com.example.moneytracker.interfaces.ITransactionRepository;import javax.inject.Singleton;import dagger.Binds;import dagger.Module;import dagger.hilt.InstallIn;import dagger.hilt.components.SingletonComponent;@Module@InstallIn(SingletonComponent.class)public abstract class RepositoryModule {    @Binds    @Singleton    public abstract ITransactionRepository bindTransactionRepository(            TransactionRepository transactionRepository    );    @Binds    @Singleton    public abstract ICategoryRepository bindCategoryRepository(            CategoryRepository categoryRepository    );    @Binds    @Singleton    public abstract IAccountRepository bindAccountRepository(            AccountRepository accountRepository    );}