package com.example.moneytracker.interfaces;import androidx.lifecycle.LiveData;import com.example.moneytracker.data.db.Transaction;import java.util.List;public interface ITransactionRepository {    LiveData<List<Transaction>> getTransactionsInDateRange(long startDate, long endDate);    LiveData<Double> getTotalAmountByTypeAndDateRange(String transactionType, long startDate, long endDate);    LiveData<Double> getTotalBalanceUpToDate(long endDate);    LiveData<Transaction> getTransactionById(int transactionId);    void insertTransaction(Transaction transaction);    void updateTransaction(Transaction transaction);    void deleteTransaction(Transaction transaction);    LiveData<List<Transaction>> getAllTransactions();    LiveData<List<Transaction>> getTransactionsByDateAndQuery(long startDate, long endDate, String query);}