package com.example.moneytracker.ui.adapters;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import androidx.annotation.NonNull;import androidx.core.content.ContextCompat;import androidx.recyclerview.widget.RecyclerView;import com.example.moneytracker.R;import java.util.List;public class IconSelectionAdapter extends RecyclerView.Adapter<IconSelectionAdapter.IconViewHolder> {    private final List<String> iconNames;    private String selectedIconName;    private final OnIconClickListener listener;    public interface OnIconClickListener {        void onIconClick(String iconName);    }    public IconSelectionAdapter(List<String> iconNames, String selectedIconName, OnIconClickListener listener) {        this.iconNames = iconNames;        this.selectedIconName = selectedIconName;        this.listener = listener;    }    @NonNull    @Override    public IconViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view = LayoutInflater.from(parent.getContext())                .inflate(R.layout.item_icon_selection, parent, false);        return new IconViewHolder(view);    }    @Override    public void onBindViewHolder(@NonNull IconViewHolder holder, int position) {        String iconName = iconNames.get(position);        holder.bind(iconName, iconName.equals(selectedIconName), listener);    }    @Override    public int getItemCount() {        return iconNames.size();    }    public void setSelectedIconName(String iconName) {        String oldSelected = this.selectedIconName;        this.selectedIconName = iconName;        if (oldSelected != null) {            notifyItemChanged(iconNames.indexOf(oldSelected));        }        notifyItemChanged(iconNames.indexOf(iconName));    }    static class IconViewHolder extends RecyclerView.ViewHolder {        private final ImageView iconImage;        private final View selectionOverlay;        public IconViewHolder(@NonNull View itemView) {            super(itemView);            iconImage = itemView.findViewById(R.id.image_view_icon);            selectionOverlay = itemView.findViewById(R.id.view_selection_overlay);        }        public void bind(String iconName, boolean isSelected, OnIconClickListener listener) {            Context context = itemView.getContext();            int resId = context.getResources().getIdentifier(iconName, "drawable", context.getPackageName());            if (resId != 0) {                iconImage.setImageResource(resId);            }            if (isSelected) {                selectionOverlay.setVisibility(View.VISIBLE);                int honeyColor = ContextCompat.getColor(context, R.color.honey_primary);                iconImage.setColorFilter(honeyColor, android.graphics.PorterDuff.Mode.SRC_IN);            } else {                selectionOverlay.setVisibility(View.GONE);                iconImage.clearColorFilter();            }            itemView.setOnClickListener(v -> listener.onIconClick(iconName));        }    }}