package com.example.moneytracker.viewmodel;import androidx.lifecycle.LiveData;import androidx.lifecycle.ViewModel;import com.example.moneytracker.data.db.Account;import com.example.moneytracker.data.model.AccountWithBalance;import com.example.moneytracker.interfaces.IAccountRepository;import java.util.List;import javax.inject.Inject;import dagger.hilt.android.lifecycle.HiltViewModel;@HiltViewModelpublic class AccountsViewModel extends ViewModel {    private final IAccountRepository repository;    public final LiveData<List<AccountWithBalance>> allAccountsWithBalance;    @Inject    public AccountsViewModel(IAccountRepository repository) {        this.repository = repository;        this.allAccountsWithBalance = repository.getAccountsWithBalance();    }    public void insert(Account account) {        repository.insert(account);    }    public void update(Account account) {        repository.update(account);    }    public void delete(Account account) {        repository.delete(account);    }}