package com.example.moneytracker.viewmodel;import androidx.lifecycle.LiveData;import androidx.lifecycle.ViewModel;import com.example.moneytracker.data.db.Category;import com.example.moneytracker.data.repository.CategoryRepository;import com.example.moneytracker.interfaces.ICategoryRepository;import java.util.List;import javax.inject.Inject;import dagger.hilt.android.lifecycle.HiltViewModel;@HiltViewModelpublic class CategoriesViewModel extends ViewModel {    private final ICategoryRepository repository;    public final LiveData<List<Category>> allCategories;    @Inject    public CategoriesViewModel(ICategoryRepository repository) {        this.repository = repository;        this.allCategories = repository.getAllCategories();    }    public void insert(Category category) {        repository.insert(category);    }    public void update(Category category) {        repository.update(category);    }    public void delete(Category category) {        repository.delete(category);    }    public LiveData<Category> getCategoryById(int categoryId) {        return repository.getCategoryById(categoryId);    }}